mapper.select:
SELECT p.positionid,p.aid,p.positionname as sypositionname,p.positionaddress as sypositionaddress,p.maxnum,p.request,
p.positioncontent,p.positionstate, p.professid, p.positionname,p.positionexper,
p.money,p.positionaddress,p.industryid,p.degreeid,p.positiontime,c.companynum,c.companyname
FROM tbl_position p,tbl_company c,tbl_admin a
where a.aid=p.aid and a.cid=c.cid and c.cid=#{cid}
        order by p.positiontime desc ;
